import React, { useState, useEffect } from 'react';
import './Booknow.css';
import Axios from 'axios';

const BookNowPage = () => {
  const [listOfUsers, setListOfUsers] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await Axios.get("http://localhost:5000/getUsers");
      setListOfUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const createUser = async () => {
    try {
      await Axios.post("http://localhost:5000/createUser", {
        name,
        email,
        date,
        time,
      });
      fetchUsers();
    } catch (error) {
      console.error("Error creating user:", error);
    }
  };

  return (
    <div className="book-now-container">
      <div
        className="book-now-bg"
        style={{
          position: 'relative',
          height: '100vh',
          background: `url('./images/c11.jpg') center/cover`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
          filter: 'blur(5px)',
        }}
      ></div>
      <div className="book-now-page">
        <div className="form-group">
          {listOfUsers.map((user, index) => (
            <div key={index}>
              <h1>Name: {user.name}</h1>
              <h1>Email: {user.email}</h1>
              <h1>Date: {user.date}</h1>
              <h1>Time: {user.time}</h1>
            </div>
          ))}
        </div>

        <h2>Book Now</h2>

        <input
          type="text"
          placeholder="Enter your name"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />

        <input
          type="email"
          placeholder="Enter your email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        <input
          type="date"
          placeholder="Select a date"
          id="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />

        <input
          type="time"
          placeholder="Select a time"
          id="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          required
        />

        <button className="submit-button" type="button" onClick={createUser}>
          SUBMIT
        </button>
      </div>
    </div>
  );
};

export default BookNowPage;
